<?phpclass MP_Emmet_Plugin_Features {	public function __construct() {		add_action( 'mp_emmet_section_features', array( $this, 'get_html' ) );	}	/*	 * Get default sidebar	 */	public function get_default_sidebar() {		$args     = array(			'before_title'  => '',			'after_title'   => '',			'before_widget' => '<div id="%1$s" class="widget %1$s col-xs-12 col-sm-12 col-md-12 col-lg-12">',			'after_widget'  => '</div>',		);		$instance = array(			'text'      => __( 'The design is clean thanks to a pleasant color scheme, readable typography and smartly organized content elements.', 'mp-emmet' ),			'name'      => __( 'Simplicity', 'mp-emmet' ),			'link'      => '#',			'button'    => __( 'Read more', 'mp-emmet' ),			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/featureslike.png'		);		wp_cache_delete( 'theme-widget-features', 'widget' );		the_widget( 'MP_Emmet_Plugin_Widget_Features', $instance, $args );		$instance = array(			'text'      => __( 'The simplicity of navigation through the theme will definitely increase the User experience across various devices.', 'mp-emmet' ),			'name'      => __( 'user friendly', 'mp-emmet' ),			'link'      => '#',			'button'    => __( 'Read more', 'mp-emmet' ),			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/featuresuser.png'		);		the_widget( 'MP_Emmet_Plugin_Widget_Features', $instance, $args );		?>		<div class="clearfix visible-sm-block"></div> <?php		$instance = array(			'text'      => __( 'Emmet is a well-coded lightweight theme, which is loading fast and positively affect the search rankings of the site.', 'mp-emmet' ),			'name'      => __( 'extremely fast', 'mp-emmet' ),			'link'      => '#',			'button'    => __( 'Read more', 'mp-emmet' ),			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/featuresfast.png'		);		the_widget( 'MP_Emmet_Plugin_Widget_Features', $instance, $args );		$instance = array(			'text'      => __( 'A full arsenal of handy admin tools will make a customization process a breeze even for non-tech users.', 'mp-emmet' ),			'name'      => __( 'flexible configuration', 'mp-emmet' ),			'link'      => '#',			'button'    => __( 'Read more', 'mp-emmet' ),			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/featuresconfiguration.png'		);		the_widget( 'MP_Emmet_Plugin_Widget_Features', $instance, $args );	}	/*	 * Get sidebar	 */	public function get_sidebar() {		$mp_emmet_features_animation = esc_attr( get_theme_mod( 'theme_features_animation', 'fadeInUp' ) );		?>		<div class="animated <?php		if ( $mp_emmet_features_animation != 'none' ): echo 'anHidden"  data-animation="' . $mp_emmet_features_animation;		endif;		?>">			<?php			/*			 * mp_emmet_before_sidebar_features hook			 *			 * @hooked mp_emmet_before_sidebar_features - 10			 */			do_action( 'mp_emmet_before_sidebar_features' );			?>			<div class="row">				<?php				if ( is_active_sidebar( 'sidebar-features' ) ) :					dynamic_sidebar( 'sidebar-features' );				else:					$this->get_default_sidebar();				endif;				?>			</div>			<?php			/*			 * mp_emmet_after_sidebar_features hook			 *			 * @hooked mp_emmet_after_sidebar_features - 10			 */			do_action( 'mp_emmet_after_sidebar_features' );			?>		</div>		<?php	}	/*	 * Get title	 */	public function get_title() {		$mp_emmet_features_title = esc_html( get_theme_mod( 'theme_features_title' ) );		if ( get_theme_mod( 'theme_features_title', false ) === false ) :			?>			<h2 class="section-title"><?php _e( 'features', 'mp-emmet' ); ?></h2>			<?php		else:			if ( ! empty( $mp_emmet_features_title ) ):				?>				<h2 class="section-title"><?php echo $mp_emmet_features_title; ?></h2>				<?php			endif;		endif;	}	/*	 * Get description	 */	public function get_description() {		$mp_emmet_features_description = wp_kses( get_theme_mod( 'theme_features_description' ), mp_emmet_plugin_allowed_html() );		if ( get_theme_mod( 'theme_features_description', false ) === false ) :			?>			<div class="section-description"><?php _e( 'What makes emmet theme unique', 'mp-emmet' ); ?></div>			<?php		else:			if ( ! empty( $mp_emmet_features_description ) ):				?>				<div class="section-description"><?php echo $mp_emmet_features_description; ?></div>				<?php			endif;		endif;	}	/*	 * features section	 */	public function get_html() {		$mp_emmet_features_id_option = esc_attr( get_theme_mod( 'theme_features_id' ) );		$mp_emmet_features_id        = empty( $mp_emmet_features_id_option ) ? 'features' : esc_attr( get_theme_mod( 'theme_features_id' ) );		?>		<section id="<?php echo $mp_emmet_features_id; ?>" class="features-section white-section default-section">			<div class="container">				<div class="section-content">					<?php					$this->get_title();					$this->get_description();					$this->get_sidebar();					?>				</div>		</section>		<?php	}}