<?phpclass MP_Emmet_Plugin_Install {	public function __construct() {		add_action( 'mp_emmet_section_install', array( $this, 'get_html' ) );	}	/*	 * Get buttons	 */	public function get_buttons() {		$mp_emmet_install_brandbutton_label = esc_html( get_theme_mod( 'theme_install_brandbutton_label', __( 'Features', 'mp-emmet' ) ) );		$mp_emmet_install_brandbutton_url   = esc_url( get_theme_mod( 'theme_install_brandbutton_url', '#' ) );		$mp_emmet_install_whitebutton_label = esc_html( get_theme_mod( 'theme_install_whitebutton_label', __( 'Read more', 'mp-emmet' ) ) );		$mp_emmet_install_whitebutton_url   = esc_url( get_theme_mod( 'theme_install_whitebutton_url', '#' ) );		?>		<div class="section-buttons">			<?php			if ( ! empty( $mp_emmet_install_brandbutton_label ) && ! empty( $mp_emmet_install_brandbutton_url ) ):				?>				<a href="<?php echo $mp_emmet_install_brandbutton_url; ?>"				   title="<?php echo $mp_emmet_install_brandbutton_label; ?>"				   class="button"><?php echo $mp_emmet_install_brandbutton_label; ?></a>				<?php			endif;			if ( ! empty( $mp_emmet_install_whitebutton_label ) && ! empty( $mp_emmet_install_whitebutton_url ) ):				?>				<a href="<?php echo $mp_emmet_install_whitebutton_url; ?>"				   title="<?php echo $mp_emmet_install_whitebutton_label; ?>"				   class="button white-button"><?php echo $mp_emmet_install_whitebutton_label; ?></a>				<?php			endif;			?>		</div>		<?php	}	/*	 * Get title	 */	public function get_title() {		$mp_emmet_install_title = esc_html( get_theme_mod( 'theme_install_title' ) );		if ( get_theme_mod( 'theme_install_title', false ) === false ) :			?>			<h3 class="section-title"><?php _e( 'Install Emmet theme now!', 'mp-emmet' ); ?></h3>			<?php		else:			if ( ! empty( $mp_emmet_install_title ) ):				?>				<h3 class="section-title"><?php echo $mp_emmet_install_title; ?></h3>				<?php			endif;		endif;	}	/*	 * Get description	 */	public function get_description() {		$mp_emmet_install_description = wp_kses( get_theme_mod( 'theme_install_description' ), mp_emmet_plugin_allowed_html() );		if ( get_theme_mod( 'theme_install_description', false ) === false ) :			?>			<div				class="section-description"><?php _e( 'The installation process is a real ease, it involves a couple of fast and clear steps to be fully completed. <br>Once you have uploaded the theme to your website and activated it, you will be presented with a detailed guided tour.', 'mp-emmet' ); ?></div>			<?php		else:			if ( ! empty( $mp_emmet_install_description ) ):				?>				<div class="section-description"><?php echo $mp_emmet_install_description; ?></div>				<?php			endif;		endif;	}	/*	 * install section	 */	public function get_html() {		$mp_emmet_install_animation = esc_attr( get_theme_mod( 'theme_install_animation', 'fadeIn' ) );		$mp_emmet_install_id_option = esc_attr( get_theme_mod( 'theme_install_id' ) );		$mp_emmet_install_id        = empty( $mp_emmet_install_id_option ) ? 'install' : esc_attr( get_theme_mod( 'theme_install_id' ) );		?>		<section id="<?php echo $mp_emmet_install_id; ?>" class="install-section transparent-section">			<div class="container">				<div class="section-content  <?php if ( $mp_emmet_install_animation != 'none' ): ?> animated anHidden"				     data-animation="<?php echo $mp_emmet_install_animation; ?> <?php endif; ?>">					<?php					$this->get_title();					$this->get_description();					$this->get_buttons();					?>				</div>			</div>		</section>		<?php	}}