<?phpclass MP_Emmet_Plugin_Map {	public function __construct() {		add_action( 'mp_emmet_section_map', array( $this, 'get_html' ) );	}	/*	 * Get default sidebar	 */	public function get_default_sidebar() {		$args     = array(			'before_title'  => '',			'after_title'   => '',			'before_widget' => '',			'after_widget'  => '',		);		$instance = array(			'latidute'  => '40.708709',			'longitude' => '-74.008963',			'text'      => '<strong>Address:</strong> 123 Street W, Seattle WA 99999<br/><strong>Phone:</strong> 123-456-7890<br/><strong>Open:</strong> 8:30 am - 9:00 pm'		);		wp_cache_delete( 'theme-widget-googlemap', 'widget' );		the_widget( 'MP_Emmet_Plugin_Widget_GoogleMap', $instance, $args );	}	/*	 * Get sidebar	 */	public function get_sidebar() {		/*		 * mp_emmet_before_sidebar_googlemap hook		 *		 * @hooked mp_emmet_before_sidebar_googlemap - 10		 */		do_action( 'mp_emmet_before_sidebar_googlemap' );		?>		<?php		if ( is_active_sidebar( 'sidebar-googlemap' ) ) :			dynamic_sidebar( 'sidebar-googlemap' );		else:			$this->get_default_sidebar();		endif;		?>		<?php		/*		 * mp_emmet_after_sidebar_googlemap hook		 *		 * @hooked mp_emmet_after_sidebar_googlemap - 10		 */		do_action( 'mp_emmet_after_sidebar_googlemap' );	}	/*	 * googlemap section	 */	public function get_html() {		$mp_emmet_googlemap_id_option = esc_attr( get_theme_mod( 'theme_googlemap_id' ) );		$mp_emmet_googlemap_id        = empty( $mp_emmet_googlemap_id_option ) ? 'googlemap' : esc_attr( get_theme_mod( 'theme_googlemap_id' ) );		?>		<div id="<?php echo $mp_emmet_googlemap_id; ?>" class="googlemap-section">			<div class="section-content">				<?php				$this->get_sidebar();				?>			</div>		</div>		<?php	}}