<?phpclass MP_Emmet_Plugin_Team {	public function __construct() {		add_action( 'mp_emmet_section_team', array( $this, 'get_html' ) );	}	/*	 * Get default sidebar	 */	public function get_default_sidebar() {		$args     = array(			'before_title'  => '',			'after_title'   => '',			'before_widget' => '<div class="col-xs-12 col-sm-12 col-md-12 col-lg-12">',			'after_widget'  => '</div>',		);		$instance = array(			'text'      => __( 'ceo', 'mp-emmet' ),			'name'      => __( 'Michael Brown', 'mp-emmet' ),			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/avatar1.png'		);		wp_cache_delete( 'theme-widget-team', 'widget' );		the_widget( 'MP_Emmet_Plugin_Widget_Team', $instance, $args );		$instance = array(			'text'      => __( 'lead designer', 'mp-emmet' ),			'name'      => __( 'Sammy lee', 'mp-emmet' ),			'link'      => '#',			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/avatar2.png'		);		the_widget( 'MP_Emmet_Plugin_Widget_Team', $instance, $args );		?>		<div class="clearfix visible-xs-block"></div> <?php		$instance = array(			'text'      => __( 'manager', 'mp-emmet' ),			'name'      => __( 'James Rolph', 'mp-emmet' ),			'link'      => '#',			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/avatar3.png'		);		the_widget( 'MP_Emmet_Plugin_Widget_Team', $instance, $args );		$instance = array(			'text'      => __( 'lead programer', 'mp-emmet' ),			'name'      => __( 'Laura Izaura', 'mp-emmet' ),			'link'      => '#',			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/avatar4.png'		);		the_widget( 'MP_Emmet_Plugin_Widget_Team', $instance, $args );		?>		<div class="clearfix visible-xs-block"></div> <?php		$instance = array(			'text'      => __( 'SEO-specialist', 'mp-emmet' ),			'name'      => __( 'Ben Robinson', 'mp-emmet' ),			'link'      => '#',			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/avatar5.png'		);		the_widget( 'MP_Emmet_Plugin_Widget_Team', $instance, $args );		$instance = array(			'text'      => __( 'office-manager', 'mp-emmet' ),			'name'      => __( 'Anna Wilson', 'mp-emmet' ),			'link'      => '#',			'image_uri' => MP_EMMET_PLUGIN_PATH . 'images/avatar6.png'		);		the_widget( 'MP_Emmet_Plugin_Widget_Team', $instance, $args );	}	/*	 * Get sidebar	 */	public function get_sidebar() {		$mp_emmet_team_animation = esc_attr( get_theme_mod( 'theme_team_animation', 'fadeInUp' ) );		?>		<div class="animated  <?php		if ( $mp_emmet_team_animation === 'none' ): echo 'anHidden"  data-animation="' . $mp_emmet_team_animation;		endif;		?>">			<?php			/*			 * mp_emmet_before_sidebar_team hook			 *			 * @hooked mp_emmet_before_sidebar_team - 10			 */			do_action( 'mp_emmet_before_sidebar_team' );			?>			<div class="row">				<?php				if ( is_active_sidebar( 'sidebar-team' ) ) :					dynamic_sidebar( 'sidebar-team' );				else:					$this->get_default_sidebar();				endif;				?>				<?php				/*				 * mp_emmet_after_sidebar_team hook				 *				 * @hooked mp_emmet_after_sidebar_team - 10				 */				do_action( 'mp_emmet_after_sidebar_team' );				?>			</div>			<div class='clearfix'></div>		</div>		<?php	}	/*	 * Get title	 */	public function get_title() {		$mp_emmet_team_title = esc_html( get_theme_mod( 'theme_team_title' ) );		if ( get_theme_mod( 'theme_team_title', false ) === false ) :			?>			<h2 class="section-title"><?php _e( 'Our team', 'mp-emmet' ); ?></h2>			<?php		else:			if ( ! empty( $mp_emmet_team_title ) ):				?>				<h2 class="section-title"><?php echo $mp_emmet_team_title; ?></h2>				<?php			endif;		endif;	}	/*	 * Get description	 */	public function get_description() {		$mp_emmet_team_animation_description = esc_attr( get_theme_mod( 'theme_team_animation_description', 'fadeInLeft' ) );		$mp_emmet_team_description           = wp_kses( get_theme_mod( 'theme_team_description' ), mp_emmet_plugin_allowed_html() );		if ( get_theme_mod( 'theme_team_description', false ) === false ) :			?>			<div class="section-description <?php			if ( $mp_emmet_team_animation_description === 'none' ): echo 'animated anHidden" data-animation="' . $mp_emmet_team_animation_description;			endif;			?>">				<?php _e( 'Perfect to display the members of your staff, team or working force', 'mp-emmet' ); ?></div>			<?php		else:			if ( ! empty( $mp_emmet_team_description ) ):				?>				<div class="section-description <?php				if ( $mp_emmet_team_animation_description === 'none' ): echo 'animated anHidden" data-animation="' . $mp_emmet_team_animation_description;				endif;				?>">					<?php echo $mp_emmet_team_description; ?>				</div>				<?php			endif;		endif;	}	/*	 * team section	 */	public function get_html() {		$mp_emmet_team_id_option = esc_attr( get_theme_mod( 'theme_team_id' ) );		$mp_emmet_team_id        = empty( $mp_emmet_team_id_option ) ? 'team' : esc_attr( get_theme_mod( 'theme_team_id' ) );		?>		<section id="<?php echo $mp_emmet_team_id; ?>" class="team-section white-section default-section">			<div class="container">				<div class="section-content">					<?php					$this->get_title();					$this->get_description();					$this->get_sidebar();					?>				</div>			</div>		</section>		<?php	}}