<?phpclass MP_Emmet_Plugin_Testimonials {	public function __construct() {		add_action( 'mp_emmet_section_testimonials', array( $this, 'get_html' ) );	}	/*	 * Get default sidebar	 */	public function get_default_sidebar() {		$args     = array(			'before_title'  => '',			'after_title'   => '',			'before_widget' => '<div id="%1$s" class="widget %1$s col-xs-12 col-sm-12 col-md-12 col-lg-12">',			'after_widget'  => '</div>',		);		$instance = array(			'position'   => __( 'Imperial Corporation', 'mp-emmet' ),			'name'       => __( 'Maria Burch', 'mp-emmet' ),			'image_uri'  => MP_EMMET_PLUGIN_PATH . 'images/testimonial1.png',			'blockquote' => __( 'Awesome theme! The corporation I work for asked me to manage the company\'s blog built with Emmet and I just want to say thanks for such a good work! Iit simplified my blogging in many ways.', 'mp-emmet' )		);		wp_cache_delete( 'theme-widget-testimonial', 'widget' );		the_widget( 'MP_Emmet_Plugin_Widget_Testimonial', $instance, $args );		$instance = array(			'position'   => __( 'Alabama solutions', 'mp-emmet' ),			'name'       => __( 'Bryan Mitchell', 'mp-emmet' ),			'image_uri'  => MP_EMMET_PLUGIN_PATH . 'images/testimonial2.png',			'blockquote' => __( 'I forgot about unneeded back and forth backend moves and simply enjoy my life. I now spend more time making the music I love. Emmet has a cool visual editor, so my editing work is simply a play.', 'mp-emmet' )		);		the_widget( 'MP_Emmet_Plugin_Widget_Testimonial', $instance, $args );		$instance = array(			'position'   => __( 'INk studios', 'mp-emmet' ),			'name'       => __( 'Bryan Mitchell', 'mp-emmet' ),			'image_uri'  => MP_EMMET_PLUGIN_PATH . 'images/testimonial3.png',			'blockquote' => __( 'I run a photographer blog and the Emmet theme simply became my saver! It\'s super fast and easy to customize. I\'ve got a lot of compliments from my readers, which is my biggest satisfaction!', 'mp-emmet' )		);		the_widget( 'MP_Emmet_Plugin_Widget_Testimonial', $instance, $args );	}	/*	 * Get sidebar	 */	public function get_sidebar() {		$mp_emmet_testimonials_animation = esc_attr( get_theme_mod( 'theme_testimonials_animation', 'fadeInUp' ) );		?>		<div			class="animated  <?php if ( $mp_emmet_testimonials_animation != 'none' ): echo ' anHidden"  data-animation="' . $mp_emmet_testimonials_animation;			endif;			?>">			<?php			/*			 * mp_emmet_before_sidebar_testimonials hook			 *			 * @hooked mp_emmet_before_sidebar_testimonials - 10			 */			do_action( 'mp_emmet_before_sidebar_testimonials' );			?>			<div class="row">				<?php				if ( is_active_sidebar( 'sidebar-testimonials' ) ) :					dynamic_sidebar( 'sidebar-testimonials' );				else:					$this->get_default_sidebar();				endif;				?>				<?php				/*				 * mp_emmet_after_sidebar_testimonials hook				 *				 * @hooked mp_emmet_after_sidebar_testimonials - 10				 */				do_action( 'mp_emmet_after_sidebar_testimonials' );				?>			</div>		</div>		<?php	}	/*	 * Get title	 */	public function get_title() {		$mp_emmet_testimonials_title = esc_html( get_theme_mod( 'theme_testimonials_title' ) );		if ( get_theme_mod( 'theme_testimonials_title', false ) === false ) :			?>			<h2 class="section-title"><?php _e( 'testimonials', 'mp-emmet' ); ?></h2>			<?php		else:			if ( ! empty( $mp_emmet_testimonials_title ) ):				?>				<h2 class="section-title"><?php echo $mp_emmet_testimonials_title; ?></h2>				<?php			endif;		endif;	}	/*	 * Get description	 */	public function get_description() {		$mp_emmet_testimonials_animation_description = esc_attr( get_theme_mod( 'theme_testimonials_animation_description', 'fadeInLeft' ) );		$mp_emmet_testimonials_description           = wp_kses( get_theme_mod( 'theme_testimonials_description' ), mp_emmet_plugin_allowed_html() );		if ( get_theme_mod( 'theme_testimonials_description', false ) === false ) :			?>			<div				class="section-description <?php if ( $mp_emmet_testimonials_animation_description === 'none' ): echo 'animated anHidden" data-animation="' . $mp_emmet_testimonials_animation_description;				endif; ?>">				<?php _e( 'What our happy customers say', 'mp-emmet' ); ?></div>			<?php		else:			if ( ! empty( $mp_emmet_testimonials_description ) ):				?>				<div					class="section-description <?php if ( $mp_emmet_testimonials_animation_description === 'none' ): echo 'animated anHidden" data-animation="' . $mp_emmet_testimonials_animation_description;					endif; ?>">					<?php echo $mp_emmet_testimonials_description; ?></div>				<?php			endif;		endif;	}	/*	 * testimonials section	 */	public function get_html() {		$mp_emmet_testimonials_id_option = esc_attr( get_theme_mod( 'theme_testimonials_id' ) );		$mp_emmet_testimonials_id        = empty( $mp_emmet_testimonials_id_option ) ? 'testimonials' : esc_attr( get_theme_mod( 'theme_testimonials_id' ) );		?>		<section id="<?php echo $mp_emmet_testimonials_id; ?>"		         class="testimonials-section transparent-section default-section">			<div class="container">				<div class="section-content">					<?php					$this->get_title();					$this->get_description();					$this->get_sidebar();					?>				</div>			</div>		</section>		<?php	}}